            function shareButtons() {
                const elements = document.getElementsByClassName('sharing-platform-button');
                if(elements.length > 0) {
                    for(var i = 0; i < elements.length; i++) {
                        if(elements[i].getAttribute('class') == 'sharing-platform-button sharing-element-link') {
                            const link = elements[i].getAttribute('data-url');
                            elements[i].parentElement.addEventListener('click', function (event) {
                                toClipboard(link);
								event.preventDefault();
                            });
                        } else if(elements[i].getAttribute('class') == 'sharing-platform-button sharing-element-other') {
                            elements[i].parentElement.style.display = 'none';
                        } else {
                            const link = elements[i].getAttribute('data-href');
                            elements[i].parentElement.addEventListener('click', function (event) {
                                window.open(link, '_blank', 'height=430, width=640' );
								event.preventDefault();
                            });
                        }
                    }
                    return true;
                } else {
                    return false;
                }
            }
            shareButtons();

